package assignment3;

public class Question8 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
      int A = 1 , B = 3;
      System.out.print("Before swapping:\n A = "+ A +" \n B = "+ B );
      B=A+B;
      A=B-A;
      B=B-A;
      System.out.print("\nAfter swapping:\n A = "+ A +"\n B = "+B);
	}
    		  

}
